parameters.value:
"let url=data.env.MS_TEAMS_WEBHOOK_URL;

  let approverUrl='https://webhook.site/c055b88b-0fbd-441c-81ca-bc19b0249995';
  let productName='a';
  let productUrl='https://www.google.com';
  let subscriptionName='b';
  let subscriptionUrl='https://www.google.com';
  let requestorName='c';
  let requestorUrl='https://www.google.com';
  let requestorEmail='d';
  let requestorEmailUrl='https://www.google.com';
  let selfLink='https://www.google.com';

  let body = {
    "@type": "MessageCard",
    "@context": "http://schema.org/extensions",
    "themeColor": "0076D7",
    "summary": "Marketplace Product Subscription Approval REQUESTED",
    "sections": [{
        "activityTitle": "Marketplace Product Subscription Approval REQUESTED",
        "activitySubtitle": "Please review and approve or reject the request",
        "markdown": true,
        "facts": [{
            "name": "Product",
          	"value": "["+productName+"]("+productUrl+")"
        },{
            "name": "Subscription",
            "value": "["+subscriptionName+"]("+subscriptionUrl+")"
        },{
            "name": "Requestor Name",
            "value": "["+requestorName+"]("+requestorUrl+")"
        },{
            "name": "Requestor Email",
            "value": "["+requestorEmail+"]("+requestorEmailUrl+")"
        }],
    }],
    "potentialAction": [{
        "@type": "ActionCard",
        "name": "Comment",
        "inputs": [{
            "@type": "TextInput",
            "id": "comment",
            "isMultiline": false,
            "title": "Please enter any comments"
        }],
        "actions": [{
            "@type": "HttpPOST",
            "name": "Approve",
            "target": approverUrl,
            "body": "{\"comment\":\"{{comment.value}}\",\"approvalStatus\":\"approved\", \"selfLink\":\""+selfLink+"\",\"subscriptionName\":\""+subscriptionName+"\"}",
            "headers": [{
            	"name": "apiKey",
            	"value": data.env.API_KEY
            }]
        },{
            "@type": "HttpPOST",
            "name": "Reject",
            "target": approverUrl,
            "body": "{\"comment\":\"{{comment.value}}\",\"approvalStatus\":\"rejected\", \"selfLink\":\""+selfLink+"\",\"subscriptionName\":\""+subscriptionName+"\"}",
            "headers": [{
            	"name": "apiKey",
            	"value": data.env.API_KEY
            }]
        }]
     }]
   };

  return {url,body};"
