"let url=data.env.MS_TEAMS_WEBHOOK_URL;

  let approverUrl='https://webhook.site/c055b88b-0fbd-441c-81ca-bc19b0249995';
  let productName=data.request.body.payload.spec.product;
  let productUrl=data.env.API_CENTRAL_URL+'/products/details/'+data.request.body.payload.spec.product;
  let subscriptionName=data.request.body.payload.title;
  let subscriptionUrl=data.env.API_CENTRAL_URL+'/marketplace/subscriptions';
  let requestorName=data.response.body.result.firstname+' '+data.response.body.result.lastname;
  let requestorEmail=data.response.body.result.email;
  let requestorEmailUrl='mailTo:'+data.response.body.result.email;
  let selfLink = data.env.API_CENTRAL_URL+'/apis'+data.request.body.payload.metadata.selfLink+'/approval';

  let body = {
    "@type": "MessageCard",
    "@context": "http://schema.org/extensions",
    "themeColor": "0076D7",
    "summary": "Marketplace Product Subscription Approval REQUESTED",
    "sections": [{
        "activityTitle": "Marketplace Product Subscription Approval REQUESTED",
        "activitySubtitle": "Please review and approve or reject the request",
        "markdown": true,
        "facts": [{
            "name": "Product",
          	"value": "["+productName+"]("+productUrl+")"
        },{
            "name": "Subscription",
            "value": "["+subscriptionName+"]("+subscriptionUrl+")"
        },{
            "name": "Requestor Name",
            "value": requestorName
        },{
            "name": "Requestor Email",
            "value": "["+requestorEmail+"]("+requestorEmailUrl+")"
        }],
    }],
    "potentialAction": [{
        "@type": "ActionCard",
        "name": "Comment",
        "inputs": [{
            "@type": "TextInput",
            "id": "comment",
            "isMultiline": false,
            "title": "Please enter any comments"
        }],
        "actions": [{
            "@type": "HttpPOST",
            "name": "Approve",
            "target": approverUrl,
            "body": "{\"comment\":\"{{comment.value}}\",\"approvalStatus\":\"approved\", \"selfLink\":\""+selfLink+"\",\"subscriptionName\":\""+subscriptionName+"\",\"productName\":\""+productName+"\",\"requestorName\":\""+requestorName+"\",\"requestorEmail\":\""+requestorEmail+"\"}",
            "headers": [{
            	"name": "apiKey",
            	"value": data.env.API_KEY
            }]
        },{
            "@type": "HttpPOST",
            "name": "Reject",
            "target": approverUrl,
            "body": "{\"comment\":\"{{comment.value}}\",\"approvalStatus\":\"rejected\", \"selfLink\":\""+selfLink+"\",\"subscriptionName\":\""+subscriptionName+"\",\"productName\":\""+productName+"\",\"requestorName\":\""+requestorName+"\",\"requestorEmail\":\""+requestorEmail+"\"}",
            "headers": [{
            	"name": "apiKey",
            	"value": data.env.API_KEY
            }]
        }]
     }]
   };

  return {url,body};"

"let userId = data.request.body.payload.metadata.audit.createUserId;
  let url = 'https://platform.axway.com/api/v1/user/'+userId;
  let headers = {
    'Content-Type': 'application/json',
    Authorization: 'Bearer '+data.getTokenResponse.body.access_token
  };
  return {url,headers};"

"let dataToencode = data.env.CLIENT_ID+':'+data.env.CLIENT_SECRET;
  let buff = new Buffer(dataToencode);
  let encodedData = buff.toString('base64');

  let url = 'https://login.axway.com/auth/realms/Broker/protocol/openid-connect/token';
  let headers = {
    Authorization : 'Basic '+encodedData,
    'Content-Type': 'application/x-www-form-urlencoded'
  };
  let body = {
    grant_type: 'client_credentials'
  };

  return {url,headers,body};"
